import React, { useState } from 'react';
import ButtonUI from '../../components/Button';
import { Loader } from 'react-loader-spinner';

import './index.css';

function UploadPage() {
        const [file, setFile] = useState(null);
        const onChangeHandler  = event => {
                const file = event.target.files[0];
                console.log(file);
                console.log(this.validateSize(event));
                if(this.validateSize(event)){ 
                 setFile(file);
                }
        }
        return (
                <div className="lf_file_upload">

<form method="post" action="#" id="#">
              <div className="form-group files">
                <label>Upload Your File </label>
                <input type="file" name="file" className="form-control" onChange={onChangeHandler}/>
              </div>
              <div className="col-md-6 pull-right">
              <button width="100%" type="button" className="btn btn-info" style={{
                      color: "#fff",
                      backgroundColor: ""
              }}>Upload File</button>
              </div>
          </form>
                        <div className="lf_submit_button">
                                <ButtonUI text="Upload" backgroundColor="#000" textColor="#fff" width="200px" />
                        </div>
                        <div className="lf_upload_form">
                                <div className="lf_form_title">
                                        Enter Information:
                                </div>
                                <div className="lf_form_content">
                                        <div className="lf_form_row">
                                                <label>Title</label>
                                                <input placeholder="Enter video title" />
                                        </div>
                                        <div className="lf_form_row">
                                                <label>Description</label>
                                                <input placeholder="Enter video description" />
                                        </div>
                                </div>
                        </div>
                </div>
        )
}

export default UploadPage;
