import React, { useState } from 'react';
import { NotificationManager  } from 'react-notifications';
import { Loader } from 'react-loader-spinner';

import './index.css';

function UploadPage() {
        const [file, setFile] = useState(null);
        const [title, setTitle] = useState('');
        const [description, setDescription] = useState('');
        const [hasError, setHasError] = useState(false);
        const getExtension = filename => {
                return filename.slice(filename.lastIndexOf('.')+1);
        }
        const isVideo = (ext) => {
               return ['mp4'].indexOf(ext) !== -1;
        }
        const onChangeHandler = event => {
                const file = event.target.files[0];
                if (isVideo(getExtension(file.name))) {
                        setFile(file);
                } else {
                        NotificationManager.error('Please input a valid video file');
                }
        }

        const handleSubmit = event => {
                event.preventDefault();
                if (!title || !description || !file) {
                        NotificationManager.error("cannot fill with empty fields");
                }
        }

        return (
                <div className="lf_file_upload">
                        <form method="post">
                                <div className="form-group files">
                                        <label>Upload Your File </label>
                                        <input type="file" name="file" className="form-control" onChange={onChangeHandler} />
                                </div>
                                <div className="form-group lf_form_row">
                                        <label>Title</label>
                                        <input placeholder="Enter video title" className="form-control" onChange={(e) => setTitle(e.target.value)} />
                                </div>
                                <div className="form-group lf_form_row">
                                        <label>Description</label>
                                        <input placeholder="Enter video description" className="form-control" onChange={(e) => setDescription(e.target.value)} />
                                </div>
                                <div className="col-md-6 pull-right">
                                        <button width="100%" type="button" className="btn btn-info lf_submit_btn" onSubmit={handleSubmit}>Upload File</button>
                                </div>
                        </form>
                </div>
        )
}

export default UploadPage;
